import React, { useState } from "react";
import "react-image-lightbox/style.css";
import Lightbox from "react-image-lightbox";

export default function App() {
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  const galleryImages = [
    "/images/3.png",
    "/images/4.png",
  ];

  return (
    <div className="min-h-screen bg-white text-gray-800 px-6 py-10">
      <h1 className="text-4xl font-bold text-center text-blue-700 mb-10">
        ShareLoc'sHome ðŸš¤
      </h1>

      <section className="max-w-5xl mx-auto">
        <h2 className="text-2xl font-semibold mb-4">Galerie du bateau</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          {galleryImages.map((src, index) => (
            <img
              key={index}
              src={src}
              alt={`Bateau ${index + 1}`}
              className="rounded-xl shadow-md cursor-pointer hover:opacity-80"
              onClick={() => {
                setPhotoIndex(index);
                setLightboxOpen(true);
              }}
            />
          ))}
        </div>
        {lightboxOpen && (
          <Lightbox
            mainSrc={galleryImages[photoIndex]}
            nextSrc={galleryImages[(photoIndex + 1) % galleryImages.length]}
            prevSrc={galleryImages[(photoIndex + galleryImages.length - 1) % galleryImages.length]}
            onCloseRequest={() => setLightboxOpen(false)}
            onMovePrevRequest={() =>
              setPhotoIndex((photoIndex + galleryImages.length - 1) % galleryImages.length)
            }
            onMoveNextRequest={() =>
              setPhotoIndex((photoIndex + 1) % galleryImages.length)
            }
          />
        )}
      </section>
    </div>
  );
}
