import React, { useState, useEffect } from "react";
import AOS from "aos";
import "aos/dist/aos.css";
import "react-image-lightbox/style.css";
import Lightbox from "react-image-lightbox";
import ImageGallery from "react-image-gallery";
import "react-image-gallery/styles/css/image-gallery.css";


export default function App() {
  const [activeFAQ, setActiveFAQ] = useState(null);
  const [questionText, setQuestionText] = useState("");
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  const galleryImages = ["/images/2.png", "/images/3.png","/images/4.png"];

  useEffect(() => {
    AOS.init({ duration: 800 });
  }, []);

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  const faqList = [
    {
      question: "🪪 Faut-il un permis bateau ?",
      answer: "Oui, un permis côtier est requis pour la location de notre bateau.",
    },
    {
      question: "⛽ Le carburant est-il inclus ?",
      answer:
        "Non, le carburant est à la charge du locataire. Le bateau est remis avec le plein, il doit être rendu avec le plein également.",
    },
    {
      question: "👨‍✈️ Peut-on louer avec un skipper ?",
      answer:
        "Oui, sur demande préalable, un skipper professionnel peut vous accompagner (tarif en supplément).",
    },
    {
      question: "📍 Où se fait le départ ?",
      answer:
        "Toutes nos locations partent de la mise à l’eau de l’Estaque à Marseille. Un parking gratuit est à proximité.",
    },
  ];

  const handleQuestionSubmit = (e) => {
    e.preventDefault();
    alert("Merci pour votre question ! Nous vous répondrons rapidement.");
    setQuestionText("");
  };



  
  return (
    <div className="min-h-screen bg-white text-gray-800">
      {/* Barre de navigation */}
      <nav className="bg-white shadow-md px-8 py-4 sticky top-0 z-50">
  <div className="max-w-7xl mx-auto flex items-center justify-between">
    
    {/* Logo + sans nom */}
    <div className="flex items-center gap-4">
      <img src="/images/logo petit.png" alt="Logo ShareLoc'sHome" className="h-12 w-auto" />
    </div>

    {/* Menu de navigation */}
    <ul className="flex space-x-6 text-blue-900 font-medium text-sm md:text-base">
      <li><a href="#galerie" className="hover:text-blue-600 transition">Accueil</a></li>
      <li><a href="#about" className="hover:text-blue-600 transition">À propos</a></li>
      <li><a href="#boats" className="hover:text-blue-600 transition">Nos bateaux</a></li>
      <li><a href="#services" className="hover:text-blue-600 transition">Nos services</a></li>
      <li><a href="#avis" className="hover:text-blue-600 transition">Avis</a></li>
      <li><a href="#reservation" className="hover:text-blue-600 transition">Réservation</a></li>
      <li><a href="#faq" className="hover:text-blue-600 transition">FAQ</a></li>
      <li><a href="#contact" className="hover:text-blue-600 transition">Contact</a></li>
    </ul>
  </div>
</nav>


      <header className="text-center py-10 bg-blue-900 text-white">
        <h1 className="text-4xl font-bold mb-2">ShareLoc'sHome 🚤</h1>
        <p>Location de bateau à l’Estaque – Zodiac Medline 2C 115CV</p>
      </header>

      <section id="about" className="scroll-mt-16 py-16 px-6 max-w-5xl mx-auto" data-aos="fade-up">

  <h2 className="text-3xl font-bold mb-6 text-center">À propos du bateau</h2>
  <div className="grid md:grid-cols-2 gap-10 items-center">
  <div className="max-w-xl mx-auto">
  <div className="max-w-xl mx-auto flex gap-4">

  {/* Miniatures à gauche */}
  <div className="flex flex-col justify-center gap-3">
    {galleryImages.map((src, index) => (
      <img
        key={index}
        src={src}
        alt={`Miniature ${index + 1}`}
        className={`h-20 w-20 object-cover rounded-md cursor-pointer border-2 ${
          photoIndex === index ? "border-blue-600" : "border-transparent"
        }`}
        onClick={() => setPhotoIndex(index)}
      />
    ))}
  </div>

  {/* Image principale */}
  <div className="relative cursor-zoom-in" onClick={() => setLightboxOpen(true)}>
    <img
      src={galleryImages[photoIndex]}
      alt="Zoom"
      className="rounded-xl shadow-lg max-h-[350px] object-cover"
    />
  </div>
</div>

</div>

    <div className="flex flex-col justify-center">
      <p className="mb-4"><strong>Modèle :</strong> Zodiac Medline 2C</p>
      <p className="mb-4"><strong>Motorisation :</strong> 115CV Yamaha</p>
      <p className="mb-4"><strong>Capacité :</strong> 6 à 8 personnes</p>
      <p className="mb-4"><strong>Équipements :</strong> Bimini, échelle de bain, GPS/Sondeur, coffre de rangement, musique bluetooth, douchette</p>
      <p className="mb-4"><strong>Permis requis :</strong> Oui</p>
      <p className="mb-4"><strong>Départ :</strong> Mise à l'eau de l'Estaque, Marseille</p>
    </div>
  </div>
</section>



      <section id="galerie" className="py-16 px-6 max-w-5xl mx-auto" data-aos="fade-up">
        <h2 className="text-3xl font-bold mb-6 text-center">Galerie du bateau</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          {galleryImages.map((src, index) => (
            <img
              key={index}
              src={src}
              alt={`Photo ${index + 1}`}
              className="rounded-xl shadow-md cursor-pointer hover:opacity-80"
              onClick={() => {
                setPhotoIndex(index);
                setLightboxOpen(true);
              }}
            />
          ))}
        </div>
        {lightboxOpen && (
          <Lightbox
            mainSrc={galleryImages[photoIndex]}
            nextSrc={galleryImages[(photoIndex + 1) % galleryImages.length]}
            prevSrc={
              galleryImages[
                (photoIndex + galleryImages.length - 1) % galleryImages.length
              ]
            }
            onCloseRequest={() => setLightboxOpen(false)}
            onMovePrevRequest={() =>
              setPhotoIndex(
                (photoIndex + galleryImages.length - 1) % galleryImages.length
              )
            }
            onMoveNextRequest={() =>
              setPhotoIndex((photoIndex + 1) % galleryImages.length)
            }
          />
        )}
      </section>

      <section id="boats" className="py-16 px-6 max-w-5xl mx-auto" data-aos="fade-up">
        <h2 className="text-3xl font-bold mb-6 text-center">Nos bateaux</h2>
        <p className="text-center">Nous proposons à la location un Zodiac Medline 2C parfaitement équipé pour vos sorties en mer. Confortable, puissant, et facile à piloter.</p>
      </section>

      <section id="services" className="py-16 px-6 max-w-5xl mx-auto" data-aos="fade-up">
        <h2 className="text-3xl font-bold mb-6 text-center">Nos services</h2>
        <ul className="list-disc list-inside space-y-2">
          <li>Location à la journée ou demi-journée</li>
          <li>Skipper disponible en option</li>
          <li>Matériel de sécurité fourni</li>
          <li>Conseils pour vos itinéraires en mer</li>
        </ul>
      </section>

      <section id="avis" className="py-16 px-6 max-w-5xl mx-auto" data-aos="fade-up">
        <h2 className="text-3xl font-bold mb-6 text-center">Avis clients</h2>
        <blockquote className="text-center italic">“Une superbe journée passée sur le bateau, tout était parfait !” — Julien M.</blockquote>
      </section>

      <section id="reservation" className="py-16 px-6 max-w-5xl mx-auto" data-aos="fade-up">
        <h2 className="text-3xl font-bold mb-6 text-center">Réservation</h2>
        <p className="text-center mb-4">Contactez-nous directement via WhatsApp ou téléphone pour réserver votre sortie.</p>
        <div className="text-center">
          <a
            href="https://wa.me/33620426968"
            target="_blank"
            rel="noopener noreferrer"
            className="bg-green-600 text-white px-6 py-3 rounded-xl font-semibold hover:bg-green-500 transition"
          >
            Réserver via WhatsApp
          </a>
        </div>
      </section>

      <section className="py-16 px-6 max-w-4xl mx-auto" id="faq">
        <h2 className="text-3xl font-bold mb-8 text-center">Questions fréquentes</h2>
        <div className="space-y-4">
          {faqList.map((faq, index) => (
            <div key={index} className="border rounded-xl p-4 shadow-sm">
              <button
                onClick={() => toggleFAQ(index)}
                className="w-full text-left flex justify-between items-center font-semibold text-lg text-blue-900"
              >
                {faq.question}
                <span>{activeFAQ === index ? "−" : "+"}</span>
              </button>
              {activeFAQ === index && <p className="mt-3 text-gray-700">{faq.answer}</p>}
            </div>
          ))}
        </div>
        <div className="mt-12 text-center">
          <p className="mb-4 text-lg">
            Vous ne trouvez pas votre question ? Posez-la nous :
          </p>
          <form onSubmit={handleQuestionSubmit} className="max-w-xl mx-auto flex flex-col gap-4">
            <textarea
              value={questionText}
              onChange={(e) => setQuestionText(e.target.value)}
              placeholder="Écrivez votre question ici..."
              className="p-3 border rounded-xl"
              required
            ></textarea>
            <button
              type="submit"
              className="bg-blue-900 text-white px-6 py-3 rounded-xl font-semibold hover:bg-blue-800 transition"
            >
              Envoyer la question
            </button>
          </form>
        </div>
      </section>

      {/* Section Contact */}
      <section id="contact" className="py-16 px-6 max-w-4xl mx-auto">
        <h2 className="text-3xl font-bold mb-6 text-center">📍 Point de départ</h2>
        <p className="text-center text-lg mb-4">
          Les départs se font depuis la <strong>mise à l'eau de l'Estaque</strong>, à Marseille.
        </p>
        <p className="text-center text-sm text-gray-600 mb-8">
          Un parking gratuit est disponible à proximité pour plus de confort.
        </p>

        <div className="flex justify-center mb-6">
          <iframe
            title="Carte Estaque"
            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2891.185962248576!2d5.3033208!3d43.3713784!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12c9c76e7d0a2315%3A0x5fba816197a78bc3!2s175%20Port%20de%20la%20Lave%2C%2013016%20Marseille!5e0!3m2!1sfr!2sfr!4v1714937586592!5m2!1sfr!2sfr"
            width="100%"
            height="300"
            style={{ border: 0 }}
            allowFullScreen=""
            loading="lazy"
            referrerPolicy="no-referrer-when-downgrade"
            className="rounded-xl shadow"
          />
        </div>

        <div className="flex flex-col sm:flex-row gap-4 justify-center mt-6">
          <a
            href="https://www.google.com/maps/dir/?api=1&destination=175+Port+de+la+Lave+13016+Marseille"
            target="_blank"
            rel="noopener noreferrer"
            className="bg-blue-900 text-white px-6 py-3 rounded-xl font-semibold hover:bg-blue-800 transition text-center"
          >
            📍 Itinéraire Google Maps
          </a>
          <a
            href="https://waze.com/ul?ll=43.3713784,5.3033208&navigate=yes"
            target="_blank"
            rel="noopener noreferrer"
            className="bg-indigo-600 text-white px-6 py-3 rounded-xl font-semibold hover:bg-indigo-500 transition text-center"
          >
            🚗 Itinéraire Waze
          </a>
        </div>
      </section>

      <footer className="bg-blue-900 text-white text-center py-6 mt-12">
        <p>© {new Date().getFullYear()} ShareLoc'sHome • Tous droits réservés</p>
        <p className="text-sm mt-2">Contact : contact@sharelocshome.com | 06 20 42 69 68</p>
      </footer>
    </div>
  );
}
