import React, { useState, useEffect } from "react";
import AOS from "aos";
import "aos/dist/aos.css";
import "react-image-lightbox/style.css";
import Lightbox from "react-image-lightbox";
import ImageGallery from "react-image-gallery";
import "react-image-gallery/styles/css/image-gallery.css";
import { useLocation } from "react-router-dom";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

  
function SampleNextArrow({ onClick }) {
    return (
      <div
        className="absolute top-1/2 -right-6 transform -translate-y-1/2 cursor-pointer z-10"
        onClick={onClick}
      >
        <div className="text-3xl text-[#2389c6] hover:text-[#216a95]">‚Ä∫</div>
      </div>
    );
  }
  
  function SamplePrevArrow({ onClick }) {
    return (
      <div
        className="absolute top-1/2 -left-6 transform -translate-y-1/2 cursor-pointer z-10"
        onClick={onClick}
      >
        <div className="text-3xl text-[#2389c6] hover:text-[#216a95]">‚Äπ</div>
      </div>
    );
  }
  
  
export default function App() {
  const [activeFAQ, setActiveFAQ] = useState(null);
  const [questionText, setQuestionText] = useState("");
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  const location = useLocation();

useEffect(() => {
  if (location.hash) {
    const element = document.querySelector(location.hash);
    if (element) {
      setTimeout(() => {
        element.scrollIntoView({ behavior: "smooth", block: "start" });
      }, 100);
    }
  }
}, [location]);


  const galleryImages = ["/images/2.png", "/images/3.png","/images/4.png"];

  useEffect(() => {
    AOS.init({ duration: 800 });
  }, []);

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  const faqList = [
    {
      question: "ü™™ Faut-il un permis bateau ?",
      answer: "Oui, un permis c√¥tier est requis pour la location de notre bateau.",
    },
    {
      question: "‚õΩ Le carburant est-il inclus ?",
      answer:
        "Non, le carburant est √† la charge du locataire. Le bateau est remis avec le plein, il doit √™tre rendu avec le plein √©galement.",
    },
    {
      question: "üë®‚Äç‚úàÔ∏è Peut-on louer avec un skipper ?",
      answer:
        "Oui, sur demande pr√©alable, un skipper professionnel peut vous accompagner (tarif en suppl√©ment).",
    },
    {
      question: "üìç O√π se fait le d√©part ?",
      answer:
        "Toutes nos locations partent de la mise √† l‚Äôeau de l‚ÄôEstaque √† Marseille. Un parking gratuit est √† proximit√©.",
    },
  ];

  const handleQuestionSubmit = (e) => {
    e.preventDefault();
    alert("Merci pour votre question ! Nous vous r√©pondrons rapidement.");
    setQuestionText("");
  };

function SampleNextArrow(props) {
  const { onClick } = props;
  return (
    <div
      className="absolute -right-4 top-1/2 transform -translate-y-1/2 text-3xl text-blue-900 cursor-pointer z-10"
      onClick={onClick}
    >
      ‚û§
    </div>
  );
}

function SamplePrevArrow(props) {
  const { onClick } = props;
  return (
    <div
      className="absolute -left-4 top-1/2 transform -translate-y-1/2 text-3xl text-blue-900 cursor-pointer z-10"
      onClick={onClick}
    >
      ‚¨Ö
    </div>
  );
}


  
  return (
    <div className="min-h-screen bg-white text-gray-800">
      {/* Barre de navigation */}
      <nav className="bg-white shadow-md px-8 py-4 sticky top-0 z-50">
  <div className="max-w-7xl mx-auto flex items-center justify-between">
    
    {/* Logo + sans nom */}
    <div className="flex items-center gap-4">
      <img src="/images/logo petit.png" alt="Logo ShareLoc'sHome" className="h-12 w-auto" />
    </div>

    {/* Menu de navigation */}
    <ul className="flex space-x-6 text-gray-900 font-medium text-sm md:text-base">
      <li><a href="#accueil" className="hover:text-[#2389c6] transition">Accueil</a></li>
      <li><a href="#services" className="hover:text-[#2389c6] transition">Nos services</a></li>
      <li><a href="#about" className="hover:text-[#2389c6] transition">√Ä propos</a></li>    
      <li><a href="/InfosPratiques" className="hover:text-[#2389c6] transition">Infos pratiques</a></li>
      <li><a href="#reservation" className="hover:text-[#2389c6] transition">R√©servation</a></li>
    </ul>
  </div>
</nav>

{/* Section Accueil */}
<section
  id="accueil"
  className="relative h-[90vh] bg-cover bg-center flex items-center justify-center"
  style={{ backgroundImage: "url('/images/erevine.jpg')" }}
>
  {/* Filtre sombre en arri√®re-plan 10% */}
  <div className="absolute inset-0 bg-black/10 z-0"></div>

  {/* Contenu align√© √† droite */}
  <div className="relative z-10 max-w-6xl w-full px-6 flex justify-end">
  <div className="bg-white bg-opacity-90 rounded-3xl p-8 max-w-md text-blue-900 shadow-xl flex flex-col items-center text-center">
      <h1 className="text-3xl md:text-4xl font-bold mb-4 text-gray-900">
      Naviguez, explorez, profitez : votre bateau vous attend.
      </h1>
      <p className="text-base md:text-lg mb-6 text-gray-700">
      R√©servez facilement un bateau fiable et tout confort, 
      au d√©part de l'estaque. Id√©al pour une sortie en mer entre amis ou en famille
      </p>
      <a href="#reservation">
      <button className="bg-[#216a95] hover:bg-[#2389c6] text-white px-6 py-3 rounded-xl font-semibold transition">
  Voir les disponibilit√©s
</button>

      </a>
    </div>
  </div>
</section>

{/* Section Nos services */}
<section id="services" className="py-20 px-6 max-w-6xl mx-auto" data-aos="fade-up">
  <h2 className="text-3xl font-bold mb-10 text-center text-gray-900">Nos services</h2>
  <div className="grid md:grid-cols-3 gap-10 text-center">
    
    <div className="bg-white p-6 rounded-xl shadow-lg border">
      <div className="text-4xl mb-4">üõ•Ô∏è</div>
      <h3 className="text-xl font-semibold mb-2 text-blue-800">Location de bateau tout √©quip√©</h3>
      <p className="text-gray-700">Notre Zodiac Medline 2C est pr√™t √† naviguer : GPS, bimini, musique, douchette et tout le mat√©riel de s√©curit√© fourni.</p>
    </div>

    <div className="bg-white p-6 rounded-xl shadow-lg border">
      <div className="text-4xl mb-4">üß≠</div>
      <h3 className="text-xl font-semibold mb-2 text-blue-800">Briefing & itin√©raires personnalis√©s</h3>
      <p className="text-gray-700">Avant votre d√©part, nous vous donnons les meilleurs conseils pour d√©couvrir les calanques et criques en toute s√©curit√©.</p>
    </div>

    <div className="bg-white p-6 rounded-xl shadow-lg border">
      <div className="text-4xl mb-4">üë®‚Äç‚úàÔ∏è</div>
      <h3 className="text-xl font-semibold mb-2 text-blue-800">Skipper disponible</h3>
      <p className="text-gray-700">Pas de permis ou envie de se d√©tendre ? Un skipper exp√©riment√© peut vous accompagner (sur demande).</p>
    </div>

   {/* <div className="bg-white p-6 rounded-xl shadow-lg border">
      <div className="text-4xl mb-4">‚õΩ</div>
      <h3 className="text-xl font-semibold mb-2 text-blue-800">Carburant non inclus</h3>
      <p className="text-gray-700">Le bateau est livr√© avec le plein, vous le ramenez plein. Simple et transparent.</p>
    </div>

    <div className="bg-white p-6 rounded-xl shadow-lg border">
      <div className="text-4xl mb-4">üìç</div>
      <h3 className="text-xl font-semibold mb-2 text-blue-800">D√©part depuis l‚ÄôEstaque</h3>
      <p className="text-gray-700">Un emplacement facile d‚Äôacc√®s avec parking gratuit √† proximit√© pour plus de confort.</p>
    </div>

    <div className="bg-white p-6 rounded-xl shadow-lg border">
      <div className="text-4xl mb-4">‚òéÔ∏è</div>
      <h3 className="text-xl font-semibold mb-2 text-blue-800">Service client disponible</h3>
      <p className="text-gray-700">Tomas reste disponible avant, pendant et apr√®s votre location pour r√©pondre √† toutes vos questions.</p>
    </div>*/}
    
  </div>
  <div className="mt-16 bg-blue-50 rounded-3xl p-10 text-center shadow-xl" data-aos="fade-up">
  <h3 className="text-2xl font-bold mb-6 text-gray-900">√âv√©nements sur-mesure</h3>
  <p className="mb-6 text-gray-700 max-w-2xl mx-auto">
    Offrez une exp√©rience inoubliable sur l‚Äôeau pour tous vos moments forts. Nous organisons des sorties personnalis√©es pour c√©l√©brer :
  </p>
  <div className="grid sm:grid-cols-2 lg:grid-cols-4 gap-6 text-blue-800 font-semibold">
    <div className="bg-white rounded-xl p-4 shadow hover:shadow-md transition">üéâ EVG / EVJF</div>
    <div className="bg-white rounded-xl p-4 shadow hover:shadow-md transition">üéÇ Anniversaire</div>
    <div className="bg-white rounded-xl p-4 shadow hover:shadow-md transition">üè¢ Team Building</div>
    <div className="bg-white rounded-xl p-4 shadow hover:shadow-md transition">üåá Sortie coucher de soleil</div>
  </div>
</div>
</section>

{/* Section A propos */}
      <section id="about" 
      className="scroll-mt-40 py-16 px-6 max-w-5xl mx-auto" 
      data-aos="fade-up">

  <h2 className="text-3xl font-bold mb-6 text-center">√Ä propos du bateau</h2>
  <div className="grid md:grid-cols-2 gap-10 items-center">
  <div className="max-w-xl mx-auto">
  <div className="max-w-xl mx-auto flex gap-4">

         {/* Miniatures √† gauche */}
  <div className="flex flex-col justify-center gap-3">
    {galleryImages.map((src, index) => (
      <img
        key={index}
        src={src}
        alt={`Miniature ${index + 1}`}
        className={`h-20 w-20 object-cover rounded-md cursor-pointer border-2 ${
          photoIndex === index ? "border-blue-600" : "border-transparent"
        }`}
        onClick={() => setPhotoIndex(index)}
      />
    ))}
  </div>

        {/* Image principale */}
  <div className="relative cursor-zoom-in" onClick={() => setLightboxOpen(true)}>
    <img
      src={galleryImages[photoIndex]}
      alt="Zoom"
      className="rounded-xl shadow-lg max-h-[350px] object-cover"
    />
  </div>
</div>

</div>

    <div className="flex flex-col justify-center">
      <p className="mb-4"><strong>Mod√®le :</strong> Zodiac Medline 2C</p>
      <p className="mb-4"><strong>Motorisation :</strong> 115CV Yamaha</p>
      <p className="mb-4"><strong>Capacit√© :</strong> 6 √† 8 personnes</p>
      <p className="mb-4"><strong>√âquipements :</strong> Bimini, √©chelle de bain, GPS/Sondeur, coffre de rangement, musique bluetooth, douchette</p>
      <p className="mb-4"><strong>Permis requis :</strong> Oui</p>
      <p className="mb-4"><strong>D√©part :</strong> Mise √† l'eau de l'Estaque, Marseille</p>

      <a
  href="#reservation"
  className="bg-[#216a95] hover:bg-[#2389c6] text-white px-6 py-3 rounded-xl font-semibold transition flex flex-col items-center text-center"
>
  R√©server maintenant
</a>

    </div>
  </div>
</section>


{/* Section avis */}
<section
  id="avis"
  className="py-20 px-6 max-w-6xl mx-auto"
  data-aos="fade-up"
>
  <h2 className="text-3xl font-bold mb-10 text-center text-gray-900">Avis clients</h2>

  <Slider
    dots={true}
    infinite={true}
    speed={500}
    slidesToShow={2}
    slidesToScroll={1}
    arrows={true}
    nextArrow={<SampleNextArrow />}
    prevArrow={<SamplePrevArrow />}
    responsive={[
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
        },
      },
    ]}
  >
    {/* Avis 1 */}
    <div className="px-4">
      <div className="bg-gray-50 rounded-xl shadow-md p-6 h-full">
        <p className="text-gray-700 italic mb-4">
          ‚ÄúParfait pour une petite sortie en mer et faire du snorkeling...‚Äù
        </p>
        <div className="text-right text-sm text-gray-600">
          <p className="font-semibold">Jonathan</p>
          <p className="text-xs">03 mai 2025 ‚Äì via Click&Boat</p>
        </div>
      </div>
    </div>

    {/* Avis 2 */}
    <div className="px-4">
      <div className="bg-gray-50 rounded-xl shadow-md p-6 h-full">
        <p className="text-gray-700 italic mb-4">
          ‚ÄúTr√®s bonne journ√©e. Bateau facile √† prendre en main...‚Äù
        </p>
        <div className="text-right text-sm text-gray-600">
          <p className="font-semibold">Simon</p>
          <p className="text-xs">30 avril 2025 ‚Äì via Samboat</p>
        </div>
      </div>
    </div>

    {/* Avis 3 */}
    <div className="px-4">
      <div className="bg-gray-50 rounded-xl shadow-md p-6 h-full">
        <p className="text-gray-700 italic mb-4">‚ÄúTest‚Äù</p>
        <div className="text-right text-sm text-gray-600">
          <p className="font-semibold">Tomas</p>
          <p className="text-xs">11/05/2025 ‚Äì via Samboat</p>
        </div>
      </div>
    </div>
  </Slider>
</section>




