import React, { useState, useEffect } from "react";
import AOS from "aos";
import "aos/dist/aos.css";
import "react-image-lightbox/style.css";
import Lightbox from "react-image-lightbox";

export default function App() {
  const [activeFAQ, setActiveFAQ] = useState(null);
  const [questionText, setQuestionText] = useState("");
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  const galleryImages = ["/images/3.png", "/images/4.png"];

  useEffect(() => {
    AOS.init({ duration: 800 });
  }, []);

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  const faqList = [
    {
      question: "🪪 Faut-il un permis bateau ?",
      answer: "Oui, un permis côtier est requis pour la location de notre bateau.",
    },
    {
      question: "⛽ Le carburant est-il inclus ?",
      answer:
        "Non, le carburant est à la charge du locataire. Le bateau est remis avec le plein, il doit être rendu avec le plein également.",
    },
    {
      question: "👨‍✈️ Peut-on louer avec un skipper ?",
      answer:
        "Oui, sur demande préalable, un skipper professionnel peut vous accompagner (tarif en supplément).",
    },
    {
      question: "📍 Où se fait le départ ?",
      answer:
        "Toutes nos locations partent de la mise à l’eau de l’Estaque à Marseille. Un parking gratuit est à proximité.",
    },
  ];

  const handleQuestionSubmit = (e) => {
    e.preventDefault();
    alert("Merci pour votre question ! Nous vous répondrons rapidement.");
    setQuestionText("");
  };

  return (
    <div className="min-h-screen bg-white text-gray-800">
              {/* Barre de navigation */}
      <nav className="bg-blue-900 text-white px-6 py-4 flex justify-between items-center shadow">
        <div className="flex items-center gap-3">
          <img src="/images/logo.png" alt="logo" className="h-10" />
          <span className="font-bold text-xl">ShareLoc'sHome</span>
        </div>
        <ul className="flex gap-6 text-sm font-semibold">
          <li><a href="#galerie" className="hover:underline">Galerie</a></li>
          <li><a href="#faq" className="hover:underline">FAQ</a></li>
          <li><a href="#contact" className="hover:underline">Contact</a></li>
        </ul>
      </nav>

      <header className="text-center py-10 bg-blue-900 text-white">
        <h1 className="text-4xl font-bold mb-2">ShareLoc'sHome 🚤</h1>
        <p>Location de bateau à l’Estaque – Zodiac Medline 2C 115CV</p>
      </header>

      <section id="galerie" className="py-16 px-6 max-w-5xl mx-auto" data-aos="fade-up">
        <h2 className="text-3xl font-bold mb-6 text-center">Galerie du bateau</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          {galleryImages.map((src, index) => (
            <img
              key={index}
              src={src}
              alt={`Photo ${index + 1}`}
              className="rounded-xl shadow-md cursor-pointer hover:opacity-80"
              onClick={() => {
                setPhotoIndex(index);
                setLightboxOpen(true);
              }}
            />
          ))}
        </div>
        {lightboxOpen && (
          <Lightbox
            mainSrc={galleryImages[photoIndex]}
            nextSrc={galleryImages[(photoIndex + 1) % galleryImages.length]}
            prevSrc={
              galleryImages[
                (photoIndex + galleryImages.length - 1) % galleryImages.length
              ]
            }
            onCloseRequest={() => setLightboxOpen(false)}
            onMovePrevRequest={() =>
              setPhotoIndex(
                (photoIndex + galleryImages.length - 1) % galleryImages.length
              )
            }
            onMoveNextRequest={() =>
              setPhotoIndex((photoIndex + 1) % galleryImages.length)
            }
          />
        )}
      </section>

      <section className="py-16 px-6 max-w-4xl mx-auto" id="faq">
        <h2 className="text-3xl font-bold mb-8 text-center">Questions fréquentes</h2>
        <div className="space-y-4">
          {faqList.map((faq, index) => (
            <div key={index} className="border rounded-xl p-4 shadow-sm">
              <button
                onClick={() => toggleFAQ(index)}
                className="w-full text-left flex justify-between items-center font-semibold text-lg text-blue-900"
              >
                {faq.question}
                <span>{activeFAQ === index ? "−" : "+"}</span>
              </button>
              {activeFAQ === index && <p className="mt-3 text-gray-700">{faq.answer}</p>}
            </div>
          ))}
        </div>
        <div className="mt-12 text-center">
          <p className="mb-4 text-lg">
            Vous ne trouvez pas votre question ? Posez-la nous :
          </p>
          <form onSubmit={handleQuestionSubmit} className="max-w-xl mx-auto flex flex-col gap-4">
            <textarea
              value={questionText}
              onChange={(e) => setQuestionText(e.target.value)}
              placeholder="Écrivez votre question ici..."
              className="p-3 border rounded-xl"
              required
            ></textarea>
            <button
              type="submit"
              className="bg-blue-900 text-white px-6 py-3 rounded-xl font-semibold hover:bg-blue-800 transition"
            >
              Envoyer la question
            </button>
          </form>
        </div>
      </section>

      <footer className="bg-blue-900 text-white text-center py-6 mt-12">
        <p>© {new Date().getFullYear()} ShareLoc'sHome • Tous droits réservés</p>
        <p className="text-sm mt-2">Contact : contact@sharelocshome.com | 06 20 42 69 68</p>
      </footer>
    </div>
  );
}
