    <div className="bg-white rounded-xl p-4 shadow hover:shadow-md transition">üè¢ Team Building</div>
    <div className="bg-white rounded-xl p-4 shadow hover:shadow-md transition">üåá Sortie coucher de soleil</div>
  </div>
</div>
</section>

