import React, { useState, useEffect } from "react";
import AOS from "aos";
import "aos/dist/aos.css";
import "react-image-lightbox/style.css";
import Lightbox from "react-image-lightbox";

export default function App() {
  const [activeFAQ, setActiveFAQ] = useState(null);
  const [questionText, setQuestionText] = useState("");
  const [lightboxOpen, setLightboxOpen] = useState(false);
  const [photoIndex, setPhotoIndex] = useState(0);

  const galleryImages = ["/images/3.png", "/images/4.png"];

  useEffect(() => {
    AOS.init({ duration: 800 });
  }, []);

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  const faqList = [
    {
      question: "ü™™ Faut-il un permis bateau ?",
      answer: "Oui, un permis c√¥tier est requis pour la location de notre bateau.",
    },
    {
      question: "‚õΩ Le carburant est-il inclus ?",
      answer:
        "Non, le carburant est √† la charge du locataire. Le bateau est remis avec le plein, il doit √™tre rendu avec le plein √©galement.",
    },
    {
      question: "üë®‚Äç‚úàÔ∏è Peut-on louer avec un skipper ?",
      answer:
        "Oui, sur demande pr√©alable, un skipper professionnel peut vous accompagner (tarif en suppl√©ment).",
    },
    {
      question: "üìç O√π se fait le d√©part ?",
      answer:
        "Toutes nos locations partent de la mise √† l‚Äôeau de l‚ÄôEstaque √† Marseille. Un parking gratuit est √† proximit√©.",
    },
  ];

  const handleQuestionSubmit = (e) => {
    e.preventDefault();
    alert("Merci pour votre question ! Nous vous r√©pondrons rapidement.");
    setQuestionText("");
  };

  return (
    <div className="min-h-screen bg-white text-gray-800">
              {/* Barre de navigation */}
      <nav className="bg-blue-900 text-white px-6 py-4 flex justify-between items-center shadow">
        <div className="flex items-center gap-3">
          <img src="/images/logo.png" alt="logo" className="h-20" />
          <span className="font-bold text-xl">ShareLoc'sHome</span>
        </div>
        <ul className="flex gap-6 text-sm font-semibold">
          <li><a href="#galerie" className="hover:underline">Galerie</a></li>
          <li><a href="#faq" className="hover:underline">FAQ</a></li>
          <li><a href="#contact" className="hover:underline">Contact</a></li>
        </ul>
      </nav>

      <header className="text-center py-10 bg-blue-900 text-white">
        <h1 className="text-4xl font-bold mb-2">ShareLoc'sHome üö§</h1>
        <p>Location de bateau √† l‚ÄôEstaque ‚Äì Zodiac Medline 2C 115CV</p>
      </header>

      <section id="galerie" className="py-16 px-6 max-w-5xl mx-auto" data-aos="fade-up">
        <h2 className="text-3xl font-bold mb-6 text-center">Galerie du bateau</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
          {galleryImages.map((src, index) => (
            <img
              key={index}
              src={src}
              alt={`Photo ${index + 1}`}
              className="rounded-xl shadow-md cursor-pointer hover:opacity-80"
              onClick={() => {
                setPhotoIndex(index);
                setLightboxOpen(true);
              }}
            />
          ))}
        </div>
        {lightboxOpen && (
          <Lightbox
            mainSrc={galleryImages[photoIndex]}
            nextSrc={galleryImages[(photoIndex + 1) % galleryImages.length]}
            prevSrc={
              galleryImages[
                (photoIndex + galleryImages.length - 1) % galleryImages.length
              ]
            }
            onCloseRequest={() => setLightboxOpen(false)}
            onMovePrevRequest={() =>
              setPhotoIndex(
                (photoIndex + galleryImages.length - 1) % galleryImages.length
              )
            }
            onMoveNextRequest={() =>
              setPhotoIndex((photoIndex + 1) % galleryImages.length)
            }
          />
        )}
      </section>

      <section className="py-16 px-6 max-w-4xl mx-auto" id="faq">
        <h2 className="text-3xl font-bold mb-8 text-center">Questions fr√©quentes</h2>
        <div className="space-y-4">
          {faqList.map((faq, index) => (
            <div key={index} className="border rounded-xl p-4 shadow-sm">
              <button
                onClick={() => toggleFAQ(index)}
                className="w-full text-left flex justify-between items-center font-semibold text-lg text-blue-900"
              >
                {faq.question}
                <span>{activeFAQ === index ? "‚àí" : "+"}</span>
              </button>
              {activeFAQ === index && <p className="mt-3 text-gray-700">{faq.answer}</p>}
            </div>
          ))}
        </div>
        <div className="mt-12 text-center">
          <p className="mb-4 text-lg">
            Vous ne trouvez pas votre question ? Posez-la nous :
          </p>
          <form onSubmit={handleQuestionSubmit} className="max-w-xl mx-auto flex flex-col gap-4">
            <textarea
              value={questionText}
              onChange={(e) => setQuestionText(e.target.value)}
              placeholder="√âcrivez votre question ici..."
              className="p-3 border rounded-xl"
              required
            ></textarea>
            <button
              type="submit"
              className="bg-blue-900 text-white px-6 py-3 rounded-xl font-semibold hover:bg-blue-800 transition"
            >
              Envoyer la question
            </button>
          </form>
        </div>
      </section>
      {/* Section Contact */}
      <section id="contact" className="py-16 px-6 max-w-4xl mx-auto">
        <h2 className="text-3xl font-bold mb-6 text-center">üìç Point de d√©part</h2>
        <p className="text-center text-lg mb-4">
          Les d√©parts se font depuis la <strong>mise √† l'eau de l'Estaque</strong>, √† Marseille.
        </p>
        <p className="text-center text-sm text-gray-600 mb-8">
          Un parking gratuit est disponible √† proximit√© pour plus de confort.
        </p>

        <div className="flex justify-center mb-6">
        <iframe
  title="Carte Estaque"
  src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2891.185962248576!2d5.3033208!3d43.3713784!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12c9c76e7d0a2315%3A0x5fba816197a78bc3!2s175%20Port%20de%20la%20Lave%2C%2013016%20Marseille!5e0!3m2!1sfr!2sfr!4v1714937586592!5m2!1sfr!2sfr
"
  width="100%"
  height="300"
  style={{ border: 0 }}
  allowFullScreen=""
  loading="lazy"
  referrerPolicy="no-referrer-when-downgrade"
  className="rounded-xl shadow"
/>
        </div>

        <div className="flex flex-col sm:flex-row gap-4 justify-center mt-6">
  <a
    href="https://www.google.com/maps/dir/?api=1&destination=175+Port+de+la+Lave+13016+Marseille"
    target="_blank"
    rel="noopener noreferrer"
    className="bg-blue-900 text-white px-6 py-3 rounded-xl font-semibold hover:bg-blue-800 transition text-center"
  >
    üìç Itin√©raire Google Maps
  </a>
  
</div>

      </section>

      <footer className="bg-blue-900 text-white text-center py-6 mt-12">
        <p>¬© {new Date().getFullYear()} ShareLoc'sHome ‚Ä¢ Tous droits r√©serv√©s</p>
        <p className="text-sm mt-2">Contact : contact@sharelocshome.com | 06 20 42 69 68</p>
      </footer>
    </div>
  );
}
