import React, { useState } from "react";

export default function LocationBateau() {
  const [activeFAQ, setActiveFAQ] = useState(null);
  const [questionText, setQuestionText] = useState("");

  const toggleFAQ = (index) => {
    setActiveFAQ(activeFAQ === index ? null : index);
  };

  const faqList = [
    {
      question: "ü™™ Faut-il un permis bateau ?",
      answer: "Oui, un permis c√¥tier est requis pour la location de notre bateau."
    },
    {
      question: "‚õΩ Le carburant est-il inclus ?",
      answer: "Non, le carburant est √† la charge du locataire. Le bateau est remis avec le plein, il doit √™tre rendu avec le plein √©galement."
    },
    {
      question: "üë®‚Äç‚úàÔ∏è Peut-on louer avec un skipper ?",
      answer: "Oui, sur demande pr√©alable, un skipper professionnel peut vous accompagner (tarif en suppl√©ment)."
    },
    {
      question: "üìç O√π se fait le d√©part ?",
      answer: "Toutes nos locations partent de la mise √† l‚Äôeau de l‚ÄôEstaque √† Marseille. Un parking gratuit est √† proximit√©."
    }
  ];

  const handleQuestionSubmit = (e) => {
    e.preventDefault();
    alert("Merci pour votre question ! Nous vous r√©pondrons rapidement.");
    setQuestionText("");
  };

  return (
    <div className="min-h-screen bg-white text-gray-800">
      {/* Menu */}
      <nav className="bg-white shadow-md p-4 flex justify-between items-center sticky top-0 z-50">
        <div className="flex items-center space-x-3">
          <img src="Logo.png" alt="" className="h-10" />
          <span className="text-xl font-bold text-blue-900">ShareLoc'sHome</span>
        </div>
        <ul className="flex space-x-6 text-blue-900 font-semibold">
          <li><a href="#hero" className="hover:text-blue-600">Accueil</a></li>
          <li><a href="#about" className="hover:text-blue-600">√Ä propos</a></li>
          <li><a href="#boats" className="hover:text-blue-600">Nos bateaux</a></li>
          <li><a href="#services" className="hover:text-blue-600">Nos services</a></li>
          <li><a href="#avis" className="hover:text-blue-600">Avis</a></li>
          <li><a href="#reservation" className="hover:text-blue-600">R√©servation</a></li>
          <li><a href="#faq" className="hover:text-blue-600">FAQ</a></li>
        </ul>
      </nav>

      {/* Hero Section */}
      <section id="hero" className="relative h-screen bg-cover bg-center flex flex-col justify-center items-center text-white text-center" style={{ backgroundImage: "url('/mnt/data/3.png')" }}>
        <div className="bg-black bg-opacity-50 p-8 rounded-xl">
          <h1 className="text-4xl md:text-6xl font-bold mb-4">Notre Zodiac Medline 2C</h1>
          <p className="text-lg md:text-xl mb-6">115cv - √Ä partir de 200 ‚Ç¨/jour</p>
          <a href="#reservation">
            <button className="bg-white text-blue-900 px-6 py-3 rounded-xl font-semibold hover:bg-gray-100 transition">
              R√©server maintenant
            </button>
          </a>
        </div>
      </section>

      {/* √Ä propos du bateau */}
      <section id="about" className="py-16 px-6 max-w-5xl mx-auto">
        <h2 className="text-3xl font-bold mb-6 text-center">√Ä propos du bateau</h2>
        <div className="grid md:grid-cols-2 gap-10">
          <img src="/mnt/data/3.png" alt="Zodiac Medline 2" className="rounded-2xl shadow-lg" />
          <div>
            <p className="mb-4">Mod√®le : Zodiac Medline 2</p>
            <p className="mb-4">Motorisation : 115CV Yamaha</p>
            <p className="mb-4">Capacit√© : 6-8 personnes</p>
            <p className="mb-4">√âquipements : Bimini, √©chelle de bain, GPS/Sondeur, coffre de rangement, musique bluetooth, douchette</p>
            <p className="mb-4">Permis requis : Oui</p>
            <p className="mb-4">D√©part : Mise √† l'eau de l'Estaque</p>
          </div>
        </div>
      </section>

      {/* Carte + bouton itin√©raire */}
      <div className="text-center mt-6">
        <div className="w-full h-64 max-w-3xl mx-auto mb-6">
          <iframe
            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2897.301465096162!2d5.304359076582094!3d43.36601927209001!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x12c9c783b5f3e94d%3A0x6a792e9bc1e2898a!2sMise%20%C3%A0%20l'eau%20de%20l'Estaque!5e0!3m2!1sfr!2sfr!4v1714931224701!5m2!1sfr!2sfr"
            width="100%"
            height="100%"
            style={{ border: 0 }}
            allowFullScreen=""
            loading="lazy"
            referrerPolicy="no-referrer-when-downgrade"
          ></iframe>
        </div>
        <a
          href="https://www.google.com/maps/dir/?api=1&destination=Mise+√†+l%27eau+de+l%27Estaque,+Marseille"
          target="_blank"
          rel="noopener noreferrer"
        >
          <button className="mt-4 bg-blue-900 text-white px-6 py-3 rounded-xl font-semibold hover:bg-blue-800 transition">
            Voir l'itin√©raire Google Maps
          </button>
        </a>
      </div>

      {/* FAQ */}
      <section id="faq" className="py-16 px-6 max-w-4xl mx-auto">
        <h2 className="text-3xl font-bold mb-8 text-center">Questions fr√©quentes</h2>
        <div className="space-y-4">
          {faqList.map((faq, index) => (
            <div key={index} className="border rounded-xl p-4 shadow-sm">
              <button
                onClick={() => toggleFAQ(index)}
                className="w-full text-left flex justify-between items-center font-semibold text-lg text-blue-900"
              >
                {faq.question}
                <span>{activeFAQ === index ? "‚àí" : "+"}</span>
              </button>
              {activeFAQ === index && <p className="mt-3 text-gray-700">{faq.answer}</p>}
            </div>
          ))}
        </div>
        <div className="mt-12 text-center">
          <p className="mb-4 text-lg">Vous ne trouvez pas votre question ? Posez-la nous, nous nous ferons un plaisir d'y r√©pondre :</p>
